// Copy to clipboard
/**
 * Copy text to clipboard.
 * @param {string} text Text to copy
 * @return {*}
 */
function copyToClipboard(text) {
    if (window.clipboardData && window.clipboardData.setData)
        // Internet Explorer-specific code path to prevent textarea being shown while dialog is visible.
        return window.clipboardData.setData('Text', text);
    else if (document.queryCommandSupported && document.queryCommandSupported('copy')) {
        let textarea = document.createElement('textarea');
        textarea.textContent = text;
        textarea.style.position = 'fixed';  // Prevent scrolling to bottom of page in Microsoft Edge.
        document.body.appendChild(textarea);
        textarea.select();
        try {
            return document.execCommand('copy');  // Security exception may be thrown by some browsers.
        } catch (ex) {
            console.warn('Copy to clipboard failed.', ex);
            return prompt('Copy to clipboard: Ctrl+C, Enter', text);
        } finally {
            document.body.removeChild(textarea);
        }
    }
}


// Window params parsing

/**
 * Parse encoded url params
 * Stolen from https://stackoverflow.com/a/8486188
 * @param {string} url Encoded url str, ie a=b&c=d
 * @return {object} Map of key: value
 */
function parseWindowParams(url) {
    let question = url.indexOf('?');
    let hash = url.indexOf('#');

    if (hash === -1 && question === -1) return {};
    if (hash === -1) hash = url.length;
    let query = question === -1 || hash === question + 1 ? url.substring(hash) :
        url.substring(question + 1, hash);
    let result = {};

    query.split('&').forEach(part => {
        if (!part) return;
        part = part.split('+').join(' '); // replace every + with space, regexp-free version
        let eq = part.indexOf('=');
        let key = eq > -1 ? part.substr(0, eq) : part;
        let val = eq > -1 ? decodeURIComponent(part.substr(eq + 1)) : '';
        let from = key.indexOf('[');

        if (from === -1) result[decodeURIComponent(key)] = val;
        else {
            let to = key.indexOf(']', from);
            let index = decodeURIComponent(key.substring(from + 1, to));

            key = decodeURIComponent(key.substring(0, from));
            if (!result[key]) result[key] = [];
            if (!index) result[key].push(val);
            else result[key][index] = val;
        }
    });
    return result;
}

const windowParams = parseWindowParams(window.location.search.substr(1));

/**
 * Automatic tab detection:
 * Structure:
 * .js-tab-container
 *    .js-tabs
 *        div with tab
 *        ...
 *    div of first tab
 *    div of second tab
 *    ...
 */

/**
 * @param {DOMObject} container The .js-tab-container
 * @param {number} index Index of tab, 0 = first, 1 = 2nd, etc...
 */
function showTab(container, index) {
    for (let i = 1; i < container.children.length; i++) {
        // Remove tab highlight + hide
        container.children.item(i).style.display = 'none';
        container.children.item(0).children.item(i - 1).classList.remove('active');
    }

    container.children.item(index + 1).style.display = 'block';
    container.children.item(0).children.item(index).classList.add('active');
}

/**
 * Assign onClick events for the tabs
 * @param {DOMObject} container The .js-tab-container
 */
function createTab(container) {
    if (!container.classList.contains('js-tab-container'))
        console.warn(container, 'Does not have .js-tab-container');
    if (!container.children.item(0).classList.contains('js-tabs'))
        console.warn(container, 'First child does not have .js-tabs');

    let l1 = container.children.length;
    let l2 = container.children.item(0).children.length;

    if (l1 !== l2 + 1)
        console.error(container, `Number of tabs does not match! (${l1} != ${l2})`);

    for (let i = 0; i < container.children.item(0).children.length; i++)
        container.children.item(0).children[i].onclick = () => showTab(container, i);
}

const tabContainers = [...document.getElementsByClassName('js-tab-container')];

tabContainers.forEach(createTab);
tabContainers.forEach(c => showTab(c, 0)); // Default all tabs to first one
